import React, { useState } from 'react';
import PropTypes from 'prop-types';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Modal from 'react-bootstrap/Modal';

export default function CourseCard({
  courseJSON, setField, setTitle, setUrl, setImgUrl, setAuthor, setId,
}) {
  const [editorMode, setEditorMode] = useState(false);
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const courseData = JSON.parse(courseJSON);

  // Function to update GraphQL query parameters to trigger API call to delete course
  const deleteCourseMethod = () => {
    setId(courseData.ID);
    setField('removeCourse');
  };

  const enableEditor = () => {
    setEditorMode(true);
  };

  const disableEditor = () => {
    setEditorMode(false);
  };

  // Function to update GraphQL query parameters to trigger API call to edit course
  const confirmEdits = (event) => {
    event.preventDefault();
    if (event.target[0].value
      && event.target[1].value
      && event.target[2].value
      && event.target[3].value
    ) {
      setId(courseData.ID);
      setTitle(event.target[0].value);
      setAuthor(event.target[1].value);
      setUrl(event.target[2].value);
      setImgUrl(event.target[3].value);
      setField('editCourse');
      disableEditor();
    }
  };

  if (editorMode) {
    return (
      <Col className="col-3" style={{ paddingBottom: '24px' }}>
        <Card>
          <Card.Img
            variant="top"
            src={courseData.CoverArt}
            alt="Image Not Found"
          />
          <Card.Header>{`Editing: ${courseData.CourseName}`}</Card.Header>
          <Card.Body>
            <Form onSubmit={confirmEdits}>
              <Container className="flex">
                <InputGroup className="mb-3">
                  <Form.Control
                    placeholder="Course Title"
                    aria-label="Course Title"
                    defaultValue={courseData.CourseName}
                  />
                </InputGroup>
                <InputGroup className="mb-3">
                  <Form.Control
                    placeholder="Course Author"
                    aria-label="Course Author"
                    defaultValue={courseData.Author}
                  />
                </InputGroup>
                <InputGroup className="mb-3">
                  <Form.Control
                    placeholder="Course URL"
                    aria-label="Course URL"
                    defaultValue={courseData.CourseUrl}
                  />
                </InputGroup>
                <InputGroup className="mb-3">
                  <Form.Control
                    placeholder="Course Image URL"
                    aria-label="Course Image URL"
                    defaultValue={courseData.CoverArt}
                  />
                </InputGroup>
                <Button className="button" onClick={disableEditor}>Discard Changes</Button>
                <InputGroup className="mb-3">
                  <Button className="button" type="submit">Save Changes</Button>
                </InputGroup>
              </Container>
            </Form>
          </Card.Body>
        </Card>
      </Col>
    );
  }
  return (
    <Col className="col-3" style={{ paddingBottom: '24px' }}>
      <Card>
        <Card.Img
          variant="top"
          src={courseData.CoverArt}
          alt="Image Not Found"
        />
        <Card.Header>{courseData.CourseName}</Card.Header>
        <Card.Body>
          <Card.Text>
            <b>Author:</b>
            {` ${courseData.Author}`}
          </Card.Text>
          <Button className="button" href={`${courseData.CourseUrl}`} target="_blank">Open Course</Button>
          <Container />
          <Button className="button" onClick={handleShow}>Delete Course</Button>
          <Button className="button" onClick={enableEditor}>Edit Course</Button>
          <Modal
            show={show}
            onHide={handleClose}
            keyboard={false}
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title>
                Delete Course
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              {`Are you sure want to delete the course "${courseData.CourseName}"?`}
            </Modal.Body>
            <Modal.Footer>
              <Button className="button" variant="secondary" onClick={handleClose}>No</Button>
              <Button className="button" onClick={deleteCourseMethod}>Yes</Button>
            </Modal.Footer>
          </Modal>
        </Card.Body>
      </Card>
    </Col>
  );
}

CourseCard.propTypes = {
  courseJSON: PropTypes.string.isRequired,
  setField: PropTypes.func.isRequired,
  setTitle: PropTypes.func.isRequired,
  setUrl: PropTypes.func.isRequired,
  setImgUrl: PropTypes.func.isRequired,
  setAuthor: PropTypes.func.isRequired,
  setId: PropTypes.func.isRequired,
};
